# Loop through each EudraCT code
for (eudract_code in eudract_codes) {

  # Construct the URL using the EudraCT code
  url <- paste0("https://www.clinicaltrialsregister.eu/ctr-search/trial/", eudract_code, "/results")

  # Read the HTML content of the trial results page
  content <- read_html(url)

  # Extract all tables from the webpage
  tables <- content %>% html_table(fill = TRUE)

  # Search for the table that has "Serious adverse events" in the first row
  target_table <- NULL
  for (i in 1:length(tables)) {
    if (nrow(tables[[i]]) > 0 && "Serious adverse events" %in% tables[[i]][1, ]) {
      target_table <- tables[[i]]
      break
    }
  }

  # Update cumulative_eudractcodes_SAE
  if (!is.null(target_table)) {
    cumulative_eudractcodes_SAE[nrow(cumulative_eudractcodes_SAE) + 1, ] <- c(eudract_code, TRUE)
  } else {
    cumulative_eudractcodes_SAE[nrow(cumulative_eudractcodes_SAE) + 1, ] <- c(eudract_code, FALSE)
  }

  # Define the set of strings to check for in the first column of the target table
  strings_to_check <- c("Additional description")

  # Check every row in the first column of the target table, starting from the bottom
  if (!is.null(target_table)) {
    for (i in nrow(target_table):2) {
      if (startsWith(as.character(target_table[i, 1]), strings_to_check)) {
        # Concatenate the cell with the one above that does not match the set of strings to check
        for (j in (i-1):1) {
          if (!startsWith(as.character(target_table[j, 1]), strings_to_check)) {
            target_table[j, 1] <- paste(target_table[j, 1], " (", target_table[i, 1], ")")
            target_table[i, 1] <- NA
            break
          }
        }
      }
    }
  }

  # Remove all rows that are NA except the first row
  target_table <- target_table[!is.na(target_table[,1]),] 

  # Clean up the target table if it was found
  if (!is.null(target_table)) {
    for (i in nrow(target_table):1) {
      if (!is.na(target_table[i, 1]) && i != 1 && target_table[i, 1] == "subjects affected / exposed") {
        # Check if the cell above starts with "alternative dictionary used"
        if (i > 2 && !startsWith(as.character(target_table[i - 1, 1]), "alternative dictionary used")) {
          target_table[i, 1] <- target_table[i - 1, 1]
        } else {
          target_table[i, 1] <- target_table[i - 2, 1]
        }
      } else if (i != 1) {
        target_table[i, ] <- NA # Set all other rows to NA
      }
    }

    # Remove all rows that are NA except the first row
    target_table <- target_table[!is.na(target_table[,1]),] 

    # Extract the header and first row of target_table
    header <- colnames(target_table)[-1]
    row_1 <- as.character(target_table[1, -1])

    # Create a new data frame "results_groups" in a long format with two columns
    results_groups <- data.frame(group_code = header,
                                 group_name = row_1)



    # Add a new column with the eudract_code
    results_groups <- results_groups %>% mutate(eudract_code = eudract_code)


    # remove the first row from the target_table
    target_table <- target_table[-1,]

    # Pivot the table to a long format
    target_table_long <- pivot_longer(
      data = target_table, 
      cols = -1, 
      names_to = "Group", 
      values_to = "Values"
    )

    # Separate the values in the Values column into two columns
    target_table_long <- target_table_long %>% 
      separate(Values, into = c("affected", "exposed"), sep = " / ", convert = TRUE) %>% 
      mutate(across(c(affected, exposed), ~as.numeric(trimws(gsub("\\s*\\([^)]+\\)", "", as.character(.))))))


    # Add a new column with the eudract_code
    target_table_long <- target_table_long %>% mutate(eudract_code = eudract_code)


    cumulative_results_groups_SAE <- rbind(cumulative_results_groups_SAE, results_groups)
    cumulative_target_table_long_SAE <- rbind(cumulative_target_table_long_SAE, target_table_long)


    # Print
    print(target_table_long)
  } else {
    print("2! Table with header 'Serious adverse events' not found.")
  }
}

