function x = ClarkN_Lab7(A, b)
    % Get matrix dimensions
    n = size(A, 1);

    % Check if this is the terminal instance
    if n == 1
        x = b / A; % Solve the single equation
    else
        % Perform partial pivoting
        [~, pivot_index] = max(abs(A(:, 1))); 
        A([1, pivot_index], :) = A([pivot_index, 1], :);
        b([1, pivot_index]) = b([pivot_index, 1]);

        % Get the new A_prime and b_prime
        A_prime = A(2:end, 2:end);
        b_prime = b(2:end) - (b(1) * A(2:end, 1) / A(1, 1));

        % Form the smaller system
        A_prime = A_prime - ((A(2:end, 1) * A(1, 2:end)) / A(1, 1));

        % Recursion step: solve for the smaller system
        x_recursive = ClarkN_Lab7(A_prime, b_prime);

        % Calculate the first element of x vector based on the smaller system solution
        x1 = (b(1) - A(1, 2:end) * x_recursive) / A(1, 1);

        % Combine the results to form the final solution vector
        x = [x1; x_recursive];
    end
end

