	/**
	 * Constructor to initialize MoistureControl object.
	 * @param IdealMoistureText Field with ideal moisture value.
	 * @param IdealMoistureRangeText Field with ideal moisture range.
	 * @param SprinklerOnRateText Field with sprinkler on rate value.
	 * @param SprinklerOffRateText Field with sprinkler off rate value.
	 * @param CurrentMoistureLabel Label to display current moisture value.
	 * @param SprinklerIndicator Panel to show on/off status of sprinkler.
	 */
	public MoistureControl(JTextField IdealMoistureText, JTextField IdealMoistureRangeText, 
			JTextField SprinklerOnRateText, JTextField SprinklerOffRateText, 
			JLabel CurrentMoistureLabel, JPanel SprinklerIndicator) {
		this.IdealMoistureText = IdealMoistureText;
	    this.IdealMoistureRangeText = IdealMoistureRangeText;
	    this.SprinklerOnRateText = SprinklerOnRateText;
	    this.SprinklerOffRateText = SprinklerOffRateText;
	    this.CurrentMoistureLabel = CurrentMoistureLabel;
	    this.SprinklerIndicator = SprinklerIndicator;
	    this.sprinklerMode = SprinklerMode.OFF;
	    this.SimulationData = new StringBuilder();
	}

	/**
	 * Enum for representing modes of sprinkler: ON and OFF.
	 */
	private enum SprinklerMode{
		ON, OFF
	}

	/**
	 * Setter for current moisture.
	 * @param CurrentMoisture The current moisture value to be set.
	 */
	public void setCurrentMoisture(int CurrentMoisture) {
		this.CurrentMoisture = CurrentMoisture;
	}

	/**
	 * Getter for current moisture.
	 * @return Returns current moisture value.
	 */
	public int getCurrentMoisture() {
		return CurrentMoisture;
	}

	/**
	 * Pauses simulation.
	 * @param pause Pauses or resumes simulation.
	 */
	public void pauseSimulation(boolean pause) {
        pauseSimulation = pause;
    }

	/**
	 * Stops the thread.
	 */
	public void stopThread() {
		running = false;
	}

	/**
	 * Simulation loop for running sprinkler simulation.
	 */
	public void run() {
		while (running) {
			if (!pauseSimulation) {
				int IdealMoisture = Integer.parseInt(IdealMoistureText.getText());
			    int IdealMoistureRange = Integer.parseInt(IdealMoistureRangeText.getText());
			    int SprinklerOnRate = Integer.parseInt(SprinklerOnRateText.getText());
			    int SprinklerOffRate = Integer.parseInt(SprinklerOffRateText.getText());

				if (CurrentMoisture < IdealMoisture - IdealMoistureRange) {
					sprinklerMode = SprinklerMode.ON;
					CurrentMoisture += SprinklerOnRate;
				} else if (CurrentMoisture > IdealMoisture + IdealMoistureRange) {
					sprinklerMode = SprinklerMode.OFF;
					CurrentMoisture -= SprinklerOffRate;
				} else {
					sprinklerMode = SprinklerMode.OFF;
					CurrentMoisture -= SprinklerOffRate;
				}

				if (CurrentMoisture > 100) {
					CurrentMoisture = 100;
				} else if (CurrentMoisture < 0) {
					CurrentMoisture = 0;
				}

				CurrentMoistureLabel.setText("Current Moisture: " + CurrentMoisture);
				SprinklerIndicator.setBackground(sprinklerMode == SprinklerMode.ON ? Color.GREEN : Color.RED);

				if (sprinklerMode == SprinklerMode.OFF) {
                    SprinklerOffRateText.setBackground(Color.GREEN);
                } else {
                	SprinklerOffRateText.setBackground(Color.WHITE);
                }

				SimulationData.append("Moisture: ").append(CurrentMoisture)
                .append(", Moisture Indicator: ").append(sprinklerMode == SprinklerMode.ON ? "ON" : "OFF")
                .append('\n');

				try {
					Thread.sleep(3000);
				} catch (InterruptedException e) {
					break;
				}
			}
		}
	}
}

