				} else if (CurrentMoisture > IdealMoisture + IdealMoistureRange) {
					sprinklerMode = SprinklerMode.OFF;
					CurrentMoisture -= SprinklerOffRate;
				} else {
					sprinklerMode = SprinklerMode.OFF;
					CurrentMoisture -= SprinklerOffRate;
				}

				if (CurrentMoisture > 100) {
					CurrentMoisture = 100;
				} else if (CurrentMoisture < 0) {
					CurrentMoisture = 0;
				}

				CurrentMoistureLabel.setText("Current Moisture: " + CurrentMoisture);
				SprinklerIndicator.setBackground(sprinklerMode == SprinklerMode.ON ? Color.GREEN : Color.RED);

				if (sprinklerMode == SprinklerMode.OFF) {
                    SprinklerOffRateText.setBackground(Color.GREEN);
                } else {
                	SprinklerOffRateText.setBackground(Color.WHITE);
                }

				SimulationData.append("Moisture: ").append(CurrentMoisture)
                .append(", Moisture Indicator: ").append(sprinklerMode == SprinklerMode.ON ? "ON" : "OFF")
                .append('\n');

				try {
					Thread.sleep(3000);
				} catch (InterruptedException e) {
					break;
				}
			}
		}
	}
