		this.hvacMode = HVACMode.NONE;
    }

    /**
	 * Enum for representing modes of hvac: AC, FURNACE, and NONE.
	 */
    private enum HVACMode {
        FURNACE,
        AC,
        NONE
    }

    /**
	 * Setter for current temperature.
	 * @param CurrentTemp The current temperature value to be set.
	 */
    public void setCurrentTemp(int CurrentTemp) {
        this.CurrentTemp = CurrentTemp;
    }

    /**
	 * Getter for current temperature.
	 * @return Returns current temperature value.
	 */
    public int getCurrentTemp() {
        return CurrentTemp;
    }

    /**
	 * Pauses simulation.
	 * @param pause Pauses or resumes simulation.
	 */
    public void pauseSimulation(boolean pause) {
        pauseSimulation = pause;
    }

    /**
	 * Stops the thread.
	 */
    public void stopThread() {
        running = false;
    }

    /**
	 * Simulation loop for running hvac simulation.
	 */
    public void run() {
		while (running) {
			if (!pauseSimulation) {
				int IdealTemp = Integer.parseInt(IdealTempText.getText());
                int FurnaceRate = Integer.parseInt(FurnaceRateText.getText());
                int ACRate = Integer.parseInt(ACRateText.getText());
                int NoneRate = Integer.parseInt(NoneRateText.getText());

                int IdealTemperatureRange = 3; 

                if (CurrentTemp < IdealTemp - IdealTemperatureRange) {
                    hvacMode = HVACMode.FURNACE;
                    CurrentTemp += FurnaceRate;
                } else if (CurrentTemp > IdealTemp + IdealTemperatureRange) {
                    hvacMode = HVACMode.AC;
                    CurrentTemp -= ACRate;
                } else {
                    hvacMode = HVACMode.NONE;
                    CurrentTemp += NoneRate;
                }

                CurrentTempLabel.setText("Current Temperature: " + CurrentTemp);
                FurnaceIndicator.setBackground(hvacMode == HVACMode.FURNACE ? Color.GREEN : Color.RED);
                ACIndicator.setBackground(hvacMode == HVACMode.AC ? Color.GREEN : Color.RED);

                if (hvacMode == HVACMode.NONE) {
                    NoneRateText.setBackground(Color.GREEN);
                } else {
                    NoneRateText.setBackground(Color.WHITE);
                }
                SimulationData.append("Temperature: ").append(CurrentTemp)
                .append(", Furnace Indicator: ").append(hvacMode == HVACMode.FURNACE ? "ON" : "OFF")
                .append(", Air Conditioner Indicator: ").append(hvacMode == HVACMode.AC ? "ON" : "OFF")
                .append('\n');

				try {
					Thread.sleep(5000);
				} catch (InterruptedException e) {
					break;
				}
			}
		}
	}
