private fun scheduleUpdateLocationAlarm(context: Context) {
    val alarmManager = context.getSystemService(Context.ALARM_SERVICE) as AlarmManager
    val intent = Intent(context, UpdateLocationBroadcastReceiver::class.java)
    val prefs = context.getSharedPreferences("prefs", Context.MODE_PRIVATE)
    val access = prefs.getString("jwt", null)
    intent.putExtra(
        "jwt", access
    )
    val pendingIntent =
        PendingIntent.getBroadcast(context, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT)

    val triggerAtMillis =
        System.currentTimeMillis() + 1000 // Delay, replace 1000 with an appropriate delay time in milliseconds
    val intervalMillis = 5 * 60 * 1000L // 5 minutes

    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {
        alarmManager.setExactAndAllowWhileIdle(
            AlarmManager.RTC_WAKEUP,
            triggerAtMillis,
            pendingIntent
        )
    } else {
        alarmManager.setExact(AlarmManager.RTC_WAKEUP, triggerAtMillis, pendingIntent)
    }

    alarmManager.setRepeating(
        AlarmManager.RTC_WAKEUP,
        triggerAtMillis,
        intervalMillis,
        pendingIntent
    )
}

