% Gauss elimination method for solving linear equations
% 3 equations are: x1 + 3x2 + 5x3 = 14; 2x1 - x2 - 3x3 = 3; 4x1 + 5x2 - x3 = 7

% Define the coefficients matrix A and constants vector b
A = [1 3 5; 2 -1 -3; 4 5 -1];
b = [14; 3; 7];

% Step 1: forward elimination
n = length(b); % number of equations/unknowns

for k = 1 : n-1 % pivot equation
for i = k+1 : n % elimination equation
factor = A(i,k) / A(k,k);
A(i,k:n) = A(i,k:n) - factor * A(k,k:n); % subtract elimination equation from pivot equation
b(i) = b(i) - factor * b(k); % adjust constants vector
end
end

% Step 2: back substitution
x(n) = b(n) / A(n,n);
for k = n-1 : -1 : 1 % solve each equation for corresponding variable
x(k) = (b(k) - A(k,k+1:n) * x(k+1:n)') / A(k,k);
end

% Display the solution
fprintf(‘The solution is: x1 = %.2f, x2 = %.2f, x3 = %.2f\n’, x(1), x(2), x(3));

% Plot the solution as a 3D point
plot3(x(1), x(2), x(3), ‘ro’, ‘MarkerSize’, 10, ‘MarkerFaceColor’, ‘r’);
