public void updateRandomLocation() {
        int numRows = grid.length;
        int numCols = grid[0].length;

        int row = (int) (Math.random() * numRows);
        int col = (int) (Math.random() * numCols);

        Material currentMaterial = grid[row][col];

        switch (currentMaterial) {

            case ERASER:
                break;
            case METAL:
                break;
            case WATER:
                // Move water down if possible
                if (row < numRows - 1) {
                    Material below = grid[row + 1][col];
                    if (below == Material.ERASER) {
                        grid[row][col] = Material.ERASER;
                        grid[row + 1][col] = Material.WATER;
                    } else if (below == Material.ACID) {
                        // Water deletes acid if it falls on it
                        grid[row][col] = Material.ERASER;
                    } else if (below == Material.SMOKE) {
                        // Water moves slowly through smoke
                        if (Math.random() < 0.2) {
                            grid[row][col] = below;
                            grid[row + 1][col] = Material.WATER;
                        }
                    } else {
                        // Water displaces other liquids to the side randomly
                        int direction = (int) (Math.random() * 3) - 1; // -1, 0, or 1
                        if (col + direction >= 0 && col + direction < numCols) {
                            Material side = grid[row][col + direction];
                            if (side == Material.ERASER) {
                                grid[row][col] = Material.ERASER;
                                grid[row][col + direction] = Material.WATER;
                            } else if (side == Material.ACID) {
                                // Water deletes acid if it falls on it
                                grid[row][col] = Material.ERASER;
                            } else if (side == Material.SMOKE) {
                                // Water moves slowly through smoke
                                if (Math.random() < 0.2) {
                                    grid[row][col] = side;
                                    grid[row][col + direction] = Material.WATER;
                                }
                            }
                        }
                    }
                } else {
                    // If water reaches the bottom, pile it realistically
                    grid[row][col] = Material.WATER;
                    for (int i = row - 1; i >= 0; i--) {
                        if (grid[i][col] == Material.ERASER) {
                            grid[i][col] = Material.WATER;
                            grid[i + 1][col] = Material.ERASER;
                        } else {
                            break;
                        }
                    }
                }
                break;
            case SAND:
                // Move sand down if possible
                if (row < numRows - 1) {
                    Material below = grid[row + 1][col];
                    if (below == Material.ERASER) {
                        grid[row][col] = Material.ERASER;
                        grid[row + 1][col] = Material.SAND;
                    } else if (below == Material.WATER || below == Material.ACID) {
                        // Check if water or acid can be displaced to the side
                        int direction = (int) (Math.random() * 3) - 1; // -1, 0, or 1
                        if (col + direction >= 0 && col + direction < numCols
                                && grid[row][col + direction] == Material.ERASER) {
                            grid[row][col] = Material.ERASER;
                            grid[row][col + direction] = Material.SAND;
                        } else {
                            // If can't move liquid to the side, pile it on top of sand
                            grid[row][col] = below;
                            grid[row + 1][col] = Material.SAND;
                        }
                    } else if (below == Material.SAND) {
                        // Check if sand can be moved to the side
                        int direction = (int) (Math.random() * 3) - 1; // -1, 0, or 1
                        if (direction != 0 && col + direction >= 0 && col + direction < numCols
                                && grid[row][col + direction] == Material.ERASER) {
                            grid[row][col] = Material.ERASER;
                            grid[row][col + direction] = Material.SAND;
                        } else {
                            // If can't move sand to the side, pile it on top of sand
                            grid[row][col] = below;
                            grid[row + 1][col] = Material.SAND;
                        }
                    }
                } else {
                    // If sand reaches the bottom, pile it realistically
                    grid[row][col] = Material.SAND;
                    for (int i = row - 1; i >= 0; i--) {
                        if (grid[i][col] == Material.ERASER) {
                            grid[i][col] = Material.SAND;
                            grid[i + 1][col] = Material.ERASER;
                        } else {
                            break;
                        }
                    }
                }
                break;
            case ACID:
                // move acid down if possible
                if (row < numRows - 1
                        && grid[row + 1][col] == Material.ERASER) {
                    grid[row][col] = Material.ERASER;
                    grid[row + 1][col] = Material.ACID;
                } else if (col > 0 && grid[row][col - 1]
                        == Material.ERASER) {
                    grid[row][col] = Material.ERASER;
                    grid[row][col - 1] = Material.ACID;
                } else if (col < numCols - 1 && grid[row][col + 1]
                        == Material.ERASER) {
                    grid[row][col] = Material.ERASER;
                    grid[row][col + 1] = Material.ACID;
                }
                // corrode metal if present
                if (row < numRows - 1 && grid[row + 1][col]
                        == Material.METAL) {
                    grid[row + 1][col] = Material.ACID;
                } else if (col > 0 && grid[row][col - 1]
                        == Material.METAL) {
                    grid[row][col - 1] = Material.ACID;
                } else if (col < numCols - 1 && grid[row][col + 1]
                        == Material.METAL) {
                    grid[row][col + 1] = Material.ACID;
                }
                // delete acid if it touches water
                if (row < numRows - 1 && grid[row + 1][col]
                        == Material.WATER) {
                    grid[row][col] = Material.ERASER;
                    break;
                } else {
                    if (col > 0 && grid[row][col - 1]
                            == Material.WATER) {
                        grid[row][col] = Material.ERASER;
                    } else if (col < numCols - 1 && grid[row][col + 1]
                            == Material.WATER) {
                        grid[row][col] = Material.ERASER;
                    }
                    break;
                }
            case SMOKE:
                // Move smoke up if possible
                if (row > 0) {
                    Material above = grid[row - 1][col];
                    if (above == Material.ERASER) {
                        grid[row][col] = Material.ERASER;
                        grid[row - 1][col] = Material.SMOKE;
                    } else if ((above == Material.ACID)
                            || (above == Material.WATER)) {
                        // Check if liquid can be displaced to the side
                        int direction = (int) (Math.random() * 3) - 1; // -1, 0, or 1
                        if (col + direction >= 0 && col + direction < numCols
                                && grid[row][col + direction] == Material.ERASER) {
                            grid[row][col] = Material.ERASER;
                            grid[row][col + direction] = Material.SMOKE;
                        }
