```ruby
class PietInterpreter
 def initialize(code, input = $stdin, output = $stdout)
 @code = code
 @input = input
 @output = output
 @stack = []
 @direction = 0
 @images = [[0]]
 @width = 1
 @height = 1
 @pointer_x = 0
 @pointer_y = 0
 @waiting_for_input = False
 end

 def run
 x = 0
 y = 0
 direction_name = %w(right down left up)

 while x >= 0 && x < @width && y >= 0 && y < @height
 pixel = @images[y][x]
 color, value = get_color_and_value(pixel)

 case color
 when :white
 case value
 when 0, 1
 @stack.push(@input.getbyte) if @waiting_for_input && !@input.eof?
 @waiting_for_input = False
 when 2
 @output.print @stack.pop
 when 3
 @output.print @stack.shift
 when 4..21
 n = value - 4
 @stack.push(n) if n.between?(0, 9)
 when 22..31
 n = value - 22
 manipulated_value = @stack.pop
 case n
 when 0 then @direction = (@direction + manipulated_value) % 4
 when 1 then @direction = (@direction - manipulated_value) % 4
 when 2 then manipulate_pointer_y(-1 * manipulated_value)
 when 3 then manipulate_pointer_x(manipulated_value)
 when 4 then manipulate_pointer_y(manipulated_value)
 end
 end
 when :black
 case value
 when 0..2 then @direction = value
 when 3..5
 @direction = direction_map([1, -1, 2][@direction % 2], value - 3)
 when 6
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b + a)
 when 7
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b - a)
 when 8
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b * a)
 when 9
 a = @stack.pop || 0
 b = @stack.pop || 0
 if !a.zero?
 divisible, remainder = b.divmod(a)
 @stack.push(divisible)
 @stack.push(remainder)
 else
 @stack.push(0)
 @stack.push(0)
 end
 when 10
 val = @stack.pop || 0
 @stack.push(val == 0 ? 1 : 0)
 when 11
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b > a ? 1 : 0)
 when 12 then @stack.push(@stack.last)
 when 13 then @stack.pop
 when 14 then manipulate_pointer_x(1)
 when 15 then manipulate_pointer_y(1)
 when 16
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b / a)
 when 17
 a = @stack.pop || 0
 @stack.push(a)
 @stack.push(a)
 when 18
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b >= a ? 1 : 0)
 when 19
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b == a ? 1 : 0)
 when 20
 a = @stack.pop || 0
 b = @stack.pop || 0
 @stack.push(b <= a ? 1 : 0)
 when 21 then @waiting_for_input = true
 end
 end

 case @direction
 when 0 then x += 1
 when 1 then y += 1
 when 2 then x -= 1
 when 3 then y -= 1
 end

 if x >= @width
 @width += 1
 @images.each { |row| row << 0 }
 elsif x < 0
 @width += 1
 @pointer_x += 1
 @images.each { |row| row.unshift(0) }
 end

 if y >= @height
 @height += 1
 @images << [0] * @width
 elsif y < 0
 @height += 1
 @pointer_y += 1
 @images.unshift([0] * @width)
 end
 end
 end

 def dump
 @stack.map(&:to_s) + [@images.map { |row| row.map(&:to_s).join }.join("\n")]
 end

 def direction_name
 %w(right down left up)[@direction]
 end

 def pointer_position
 "(#{@pointer_x}, #{@pointer_y})"
 end

 private

 def get_color_and_value(pixel)
 case pixel
 when 0 then [:white, 4]
 when 1 then [:white, 5]
 when 2 then [:white, 2]
 when 3 then [:white, 3]
 when 4 then [:white, 0]
 when 5 then [:white, 1]
 when 6 then [:black, 0]
 when 7 then [:black, 1]
 when 8 then [:black, 2]
 when 9 then [:black, 3]
 when 10 then [:black, 4]
 when 11 then [:black, 5]
 when 12 then [:black, 6]
 when 13 then [:black, 7]
 when 14 then [:white, 6]
 when 15 then [:white, 7]
 when 16 then [:black, 8]
 when 17 then [:black, 9]
 else [:black, pixel - 18]
 end
 end

 def direction_map(a, b)
 ((a % 3 + b) % 3) + (2 * (a / 3))
 end

 def manipulate_pointer_x(value)
 @pointer_x += value
 if @pointer_x >= @width
 @width += 1
 @images.each { |row| row << 0 }
 elsif @pointer_x < 0
 @width += 1
 @images.each { |row| row.unshift(0) }
 end
 end

 def manipulate_pointer_y(value)
 @pointer_y += value
 if @pointer_y >= @height
 @height += 1
 @images << [0] * @width
 elsif @pointer_y < 0
 @height += 1
 @images.unshift([0] * @width)
 end
 end
end
```

