 input wire clk,
 input wire reset,
 input wire signed [31:0] op_a,
 input wire signed [31:0] op_b,
 output reg signed [31:0] result
);

reg signed [31:0] a_buf [1023:0];
reg signed [31:0] b_buf [1023:0];
reg signed [31:0] result_buf [1023:0];

initial begin
 // 将输入的操作数分别加载到a_buf和b_buf数组中
 for (i=0; i<1024; i=i+1) begin
 a_buf[i] = op_a;
 b_buf[i] = op_b;
 end
end

always @(posedge clk) begin
 if (reset) begin
